import{_ as n,c as s,a3 as o,G as i,w as l,B as c,o as d,j as e,a}from"./chunks/framework.D5KY1vER.js";const v=JSON.parse('{"title":"Copernicus Data Space Ecosystem (CDSE) openEO Federation","description":"","frontmatter":{},"headers":[],"relativePath":"Technical Documentation/Platforms/OpenEO.md","filePath":"Technical Documentation/Platforms/OpenEO.md"}'),p={name:"Technical Documentation/Platforms/OpenEO.md"};function u(h,t,f,m,g,b){const r=c("FeatureCard");return d(),s("div",null,[t[1]||(t[1]=o('<h1 id="copernicus-data-space-ecosystem-cdse-openeo-federation" tabindex="-1">Copernicus Data Space Ecosystem (CDSE) openEO Federation <a class="header-anchor" href="#copernicus-data-space-ecosystem-cdse-openeo-federation" aria-label="Permalink to &quot;Copernicus Data Space Ecosystem (CDSE) openEO Federation&quot;">‚Äã</a></h1><div class="warning custom-block"><p class="custom-block-title">üõ†Ô∏è Page Under Development</p><p>Content is being actively developed and updated for this page. EarthCODE&#39;s documentation is a living document and will be continuously updated with detailed reviews.</p></div><h2 id="about" tabindex="-1">About <a class="header-anchor" href="#about" aria-label="Permalink to &quot;About&quot;">‚Äã</a></h2>',3)),i(r,{img:"/img/platforms/platform_logos/cdse.png",alt:"CDSE openEO Federation Logo",title:"CDSE openEO Federation"},{default:l(()=>t[0]||(t[0]=[e("p",null,[a("The "),e("strong",null,"Copernicus Data Space Ecosystem (CDSE) openEO federation"),a(" provides a unified, cloud-based interface using the "),e("strong",null,"openEO API"),a(" to access and process Earth observation data at scale. It enables scientists, analysts, and developers to run reproducible, standards-based workflows.")],-1),e("ul",null,[e("li",null,[a("Unified "),e("strong",null,"access to multiple openEO processing backends"),a(" through a single, unified openEO API.")]),e("li",null,[a("Supports diverse domains by providing access to "),e("strong",null,"a wide range of Earth observation data sources"),a(", including the possibility to "),e("strong",null,"process your own data"),a(".")]),e("li",null,[a("Enables scalable workflows including "),e("strong",null,"datacube analytics, ML/AI inference, and EO product generation"),a(".")]),e("li",null,[a("Supports the development of "),e("strong",null,"interoperable workflows"),a(" thanks to the openEO standard.")])],-1)])),_:1}),t[2]||(t[2]=o('<h2 id="summary" tabindex="-1">Summary <a class="header-anchor" href="#summary" aria-label="Permalink to &quot;Summary&quot;">‚Äã</a></h2><p><strong>Infrastructure Capabilities</strong></p><table tabindex="0"><thead><tr><th>Platform</th><th>Data Access</th><th>Specialised Hardware &amp; Services</th><th>Compatible Workflows</th></tr></thead><tbody><tr><td>Copernicus Data Space Ecosystem (CDSE) openEO Federation</td><td>Offers unified access to a broad selection of Earth Observation (EO) datasets, such as Sentinel-1, Sentinel-2, Sentinel-3, Sentinel-5p, Copernicus Services, Landsat, WorldCover, and more. Users can also process their own data using openEO‚Äôs <a href="https://open-eo.github.io/openeo-python-client/api.html#openeo.rest.connection.Connection.load_stac" target="_blank" rel="noreferrer"><code>load_stac</code></a> function. For a comprehensive list of available datasets, visit <a href="https://openeofed.dataspace.copernicus.eu" target="_blank" rel="noreferrer">openeofed.dataspace.copernicus.eu</a>.</td><td>Leverages cloud infrastructure across multiple openEO backends within the federation. Details about available backends are provided on the <a href="https://documentation.dataspace.copernicus.eu/APIs/openEO/federation/openeo_federation.html#federation-members" target="_blank" rel="noreferrer">openEO Federation page</a>.</td><td>Built on the openEO API, the platform supports execution of openEO Process Graphs and User Defined Processes.</td></tr></tbody></table><p><strong>FAIR Open Science Development Capabilities</strong></p><table tabindex="0"><thead><tr><th>Platform</th><th>Development Tools</th><th>Supported Languages</th><th>Visualization Tools</th><th>Workflow Type</th></tr></thead><tbody><tr><td>Copernicus Data Space Ecosystem (CDSE) openEO Federation</td><td>openEO offers multiple tools for workflow and experiment development, including the <a href="https://openeofed.dataspace.copernicus.eu/" target="_blank" rel="noreferrer">openEO Web Editor</a> and platform independent <a href="https://openeo.org/documentation" target="_blank" rel="noreferrer">client libraries</a>. These libraries can be used in various environments, such as the <a href="http://jupyterhub.dataspace.copernicus.eu/" target="_blank" rel="noreferrer">CDSE Jupyter Notebooks</a> or on a user&#39;s local machine.</td><td>Python, R, JavaScript</td><td>The <a href="https://openeofed.dataspace.copernicus.eu/" target="_blank" rel="noreferrer">openEO Web Editor</a> enables users to visualize processing job results.</td><td>openEO process graphs as standardized, interoperable workflows/experiments</td></tr></tbody></table><h3 id="developing-workflows" tabindex="-1">Developing Workflows <a class="header-anchor" href="#developing-workflows" aria-label="Permalink to &quot;Developing Workflows&quot;">‚Äã</a></h3><p><strong>Learn By Example</strong></p><p><strong>Developing Workflows</strong></p><p><strong>Publishing to EarthCODE</strong></p><h3 id="data-access" tabindex="-1">Data Access <a class="header-anchor" href="#data-access" aria-label="Permalink to &quot;Data Access&quot;">‚Äã</a></h3><h3 id="specialised-hardware-services" tabindex="-1">Specialised Hardware &amp; Services <a class="header-anchor" href="#specialised-hardware-services" aria-label="Permalink to &quot;Specialised Hardware &amp; Services&quot;">‚Äã</a></h3><h3 id="visualization-tools" tabindex="-1">Visualization Tools <a class="header-anchor" href="#visualization-tools" aria-label="Permalink to &quot;Visualization Tools&quot;">‚Äã</a></h3><h2 id="right-sizing-and-network-of-resources" tabindex="-1">Right Sizing and Network of Resources <a class="header-anchor" href="#right-sizing-and-network-of-resources" aria-label="Permalink to &quot;Right Sizing and Network of Resources&quot;">‚Äã</a></h2><p>The CDSE openEO federation uses a credit-based system to manage resource consumption for data processing tasks. All users receive a free tier, enabling them to explore the platform and perform small-scale experiments at no cost. For larger or more intensive processing needs, additional credits can be obtained by applying for sponsorship through the <a href="https://portfolio.nor-discover.org/Service/VITO/SponsoringWizardPricelist" target="_blank" rel="noreferrer">Network of Resources (NoR)</a>. For comprehensive information on how credits are allocated and consumed, refer to the <a href="https://documentation.dataspace.copernicus.eu/APIs/openEO/credit_usage.html" target="_blank" rel="noreferrer">CDSE documentation</a>.</p><h2 id="tutorials" tabindex="-1">Tutorials <a class="header-anchor" href="#tutorials" aria-label="Permalink to &quot;Tutorials&quot;">‚Äã</a></h2><p>There are many existing examples and tutorials available to help you get started with the openEO federation on the Copernicus Data Space Ecosystem (CDSE) openEO Federation. These resources cover a wide range of topics, from basic data access to advanced processing techniques. You can find these tutorials on the <a href="https://documentation.dataspace.copernicus.eu/Usecase.html" target="_blank" rel="noreferrer">CDSE Use Cases</a>.</p><h2 id="support-and-communities" tabindex="-1">Support and Communities <a class="header-anchor" href="#support-and-communities" aria-label="Permalink to &quot;Support and Communities&quot;">‚Äã</a></h2><p>For support and community engagement, users can refer to the <a href="https://forum.dataspace.copernicus.eu/c/openeo/28" target="_blank" rel="noreferrer">CDSE openEO Forum</a> where they can ask questions, share experiences, and collaborate with other users and developers.</p><h2 id="full-documentation-can-be-found-at" tabindex="-1">Full Documentation Can be Found At <a class="header-anchor" href="#full-documentation-can-be-found-at" aria-label="Permalink to &quot;Full Documentation Can be Found At&quot;">‚Äã</a></h2><p><a href="https://documentation.dataspace.copernicus.eu/APIs/openEO/federation/openeo_federation.html" target="_blank" rel="noreferrer">https://documentation.dataspace.copernicus.eu/APIs/openEO/federation/openeo_federation.html</a></p><h1 id="openeo-a-community-standard-for-geospatial-data-processing-and-analysis" tabindex="-1">openEO: A Community Standard for Geospatial Data Processing and Analysis <a class="header-anchor" href="#openeo-a-community-standard-for-geospatial-data-processing-and-analysis" aria-label="Permalink to &quot;openEO: A Community Standard for Geospatial Data Processing and Analysis&quot;">‚Äã</a></h1><p><a href="https://openeo.org/" target="_blank" rel="noreferrer">openEO</a> represents an innovative community standard that revolutionizes geospatial data processing and analysis. This groundbreaking framework provides a novel approach to accessing, processing, and analyzing diverse Earth observation data.By adopting openEO, developers, researchers, and data scientists gain access to a unified and interoperable platform, empowering them to harness distributed computing environments and leverage cloud-based resources for addressing complex geospatial challenges.</p><h2 id="key-features-of-openeo" tabindex="-1">Key Features of openEO <a class="header-anchor" href="#key-features-of-openeo" aria-label="Permalink to &quot;Key Features of openEO&quot;">‚Äã</a></h2><p>With openEO‚Äôs collaborative nature, users can:</p><ul><li>Seamlessly share code, workflows, and data processing methods across openEO-based platforms and tools.</li><li>Foster collaboration while advancing the accessibility, scalability, and reproducibility of Earth observation data.</li><li>Utilize intuitive programming libraries that simplify the analysis of diverse Earth observation datasets.</li><li>Efficiently access and process large-scale data acros s a federation of existing cloud infrastructures, supporting: <ul><li>Exploratory research</li><li>Detailed mapping</li><li>Information extraction from Earth observation data</li></ul></li></ul><p>This streamlined approach enhances development and empowers the utilization of Earth observation data for various applications and services.</p><h2 id="access" tabindex="-1">Access <a class="header-anchor" href="#access" aria-label="Permalink to &quot;Access&quot;">‚Äã</a></h2><p>Access to the openEO federation on the Copernicus Data Space Ecosystem (CDSE), you can use the following endpoint:</p><p>The endpoint for the public service is <strong>100% open source</strong> and fully compatible with <strong>Pangeo technology</strong>.</p><ul><li><strong>Endpoint:</strong> <a href="https://openeofed.dataspace.copernicus.eu/" target="_blank" rel="noreferrer">https://openeofed.dataspace.copernicus.eu/</a></li></ul><hr><p>(TBD)</p><ul><li>Configuring OpenEO for Your Workflow</li><li>Connecting to CDSE: <a href="https://dataspace.copernicus.eu" target="_blank" rel="noreferrer">https://dataspace.copernicus.eu</a></li><li>Integration CDSE with EarthCODE Open Science Catalog</li><li>Data Access on CDSE</li></ul>',33))])}const y=n(p,[["render",u]]);export{v as __pageData,y as default};
