import{_ as e,c as r,a3 as n,o as a}from"./chunks/framework.DVH8y1P4.js";const h=JSON.parse('{"title":"Key terms and concepts","description":"","frontmatter":{"order":6},"headers":[],"relativePath":"Getting started with EarthCODE/key-terms-and-concepts.md","filePath":"Getting started with EarthCODE/key-terms-and-concepts.md"}'),o={name:"Getting started with EarthCODE/key-terms-and-concepts.md"};function i(s,t,d,c,l,g){return a(),r("div",null,t[0]||(t[0]=[n('<h1 id="key-terms-and-concepts" tabindex="-1">Key terms and concepts <a class="header-anchor" href="#key-terms-and-concepts" aria-label="Permalink to &quot;Key terms and concepts&quot;">​</a></h1><h2 id="acronyms-abbreviations-and-terms" tabindex="-1">Acronyms, Abbreviations, and Terms <a class="header-anchor" href="#acronyms-abbreviations-and-terms" aria-label="Permalink to &quot;Acronyms, Abbreviations, and Terms&quot;">​</a></h2><table tabindex="0"><thead><tr><th><strong>Term</strong></th><th><strong>Meaning</strong></th></tr></thead><tbody><tr><td><strong>DOI</strong></td><td>Digital Object Identifier</td></tr><tr><td><strong>FAIR</strong></td><td>Findable, Accessible, Interoperable, and Reusable</td></tr><tr><td><strong>Nor</strong></td><td>Network of Resources</td></tr><tr><td><strong>OGC</strong></td><td>Open Geospatial Consortium</td></tr><tr><td><strong>Config Metadata</strong></td><td>Metadata describing the configuration of a workflow, used to formally describe the configuration needed by a reproducible experiment.</td></tr><tr><td><strong>Dashboard</strong></td><td>A web application that visualizes Earth Observation data and service insights, serving as a human interface to a workflow.</td></tr><tr><td><strong>Dashboard Metadata</strong></td><td>Metadata describing a dashboard.</td></tr><tr><td><strong>Experiment (Scientific Experiment)</strong></td><td>A collection of technical artifacts that describe an experiment and how to reproduce it. This typically involves workflows implemented with code (e.g., Python, R).</td></tr><tr><td><strong>Experiment Metadata</strong></td><td>Metadata describing an experiment, potentially referencing Workflow Metadata, Input Data Metadata, and Config Metadata.</td></tr><tr><td><strong>Input Data</strong></td><td>A formal definition of data referenced using a unique ID, describing the data needed by a reproducible experiment.</td></tr><tr><td><strong>Input Data Metadata</strong></td><td>Metadata describing input data for an experiment/workflow.</td></tr><tr><td><strong>Product</strong></td><td>The output of a scientific experiment using a specific workflow implementation.</td></tr><tr><td><strong>Product Metadata</strong></td><td>Metadata describing a product.</td></tr><tr><td><strong>Workflow</strong></td><td>A formal wrapper for scientific code that can run in a specific container or environment on the cloud, often developed in languages like Python, R, or machine learning.</td></tr><tr><td><strong>Workflow Metadata</strong></td><td>Metadata describing a workflow, including required input parameters and acceptable values.</td></tr></tbody></table><h2 id="key-concepts" tabindex="-1">Key Concepts <a class="header-anchor" href="#key-concepts" aria-label="Permalink to &quot;Key Concepts&quot;">​</a></h2><table tabindex="0"><thead><tr><th><strong>Term</strong></th><th><strong>Meaning</strong></th></tr></thead><tbody><tr><td><strong>Earth Collaborative Open Development Environment (EarthCODE)</strong></td><td>A collaborative platform for developing scientific Earth Observation research projects in an open, FAIR-compliant environment.</td></tr><tr><td><strong>EarthCODE Portal</strong></td><td>A web interface for users to interact with the EarthCODE platform, focusing on accessibility and collaboration in research projects.</td></tr><tr><td><strong>Open Science Catalogue</strong></td><td>A platform for discovering and sharing scientific outputs, aligned with open science principles.</td></tr><tr><td><strong>Infrastructure Platform Services</strong></td><td>Cloud-based tools and technologies that support the execution of scientific workflows in an open, FAIR-compliant environment.</td></tr><tr><td><strong>Community Platform Services</strong></td><td>Tools that facilitate collaboration among scientists, encouraging the exchange and development of research materials.</td></tr><tr><td><strong>Development Tooling for FAIR Open Science</strong></td><td>Tools for managing the development of FAIR-compliant research materials, ensuring accessibility, reproducibility, and interoperability.</td></tr><tr><td><strong>Platform Service Onboarding</strong></td><td>The process of integrating external services and tools into the EarthCODE platform to extend its capabilities.</td></tr><tr><td><strong>Long-term Preservation Services</strong></td><td>Tools and practices designed to ensure the longevity and accessibility of scientific data and outputs over time.</td></tr><tr><td><strong>Interactive Dashboards</strong></td><td>Visual tools that allow researchers to explore and interact with data, research results, and insights in real time.</td></tr><tr><td><strong>Cloud Optimized Data Retrieval</strong></td><td>Techniques for efficiently accessing and retrieving data from cloud storage environments, particularly for large datasets in scientific workflows.</td></tr><tr><td><strong>Version Management for Resources</strong></td><td>Systems for tracking and managing different versions of scientific resources (code, data, etc.), ensuring reproducibility and consistency.</td></tr><tr><td><strong>Community Engagement Tools</strong></td><td>Tools designed to facilitate interaction and collaboration within the scientific community, enabling the sharing of knowledge and resources.</td></tr><tr><td><strong>Collaboration and Sharing Tools</strong></td><td>Tools that promote collaboration among researchers, including shared workspaces, communication tools, and version-controlled resources.</td></tr></tbody></table>',5)]))}const m=e(o,[["render",i]]);export{h as __pageData,m as default};
