import{_ as t,c as o,a3 as e,o as r}from"./chunks/framework.V29fy8yX.js";const h=JSON.parse('{"title":"Euro Data Cube","description":"","frontmatter":{},"headers":[],"relativePath":"Technical Documentation/Platforms/EDC.md","filePath":"Technical Documentation/Platforms/EDC.md"}'),i={name:"Technical Documentation/Platforms/EDC.md"};function n(s,a,l,u,c,d){return r(),o("div",null,a[0]||(a[0]=[e('<h1 id="euro-data-cube" tabindex="-1">Euro Data Cube <a class="header-anchor" href="#euro-data-cube" aria-label="Permalink to &quot;Euro Data Cube&quot;">​</a></h1><p>Find out more at: <a href="https://eurodatacube.com/documentation" target="_blank" rel="noreferrer">https://eurodatacube.com/documentation</a></p><h2 id="earth-observation-information-factory" tabindex="-1"><strong>Earth Observation Information Factory</strong> <a class="header-anchor" href="#earth-observation-information-factory" aria-label="Permalink to &quot;**Earth Observation Information Factory**&quot;">​</a></h2><p>Euro Data Cube is your <strong>one-stop-shop for EO</strong>, offering a comprehensive range of Earth Observation (EO) and derived data products in one place. It enables you to:</p><ul><li><strong>Analyze events or phenomena</strong> from multiple perspectives.</li><li>Access <strong>multiple data sources</strong> and compare or correlate variables simultaneously.</li><li><strong>Customize your data pipeline</strong> to suit specific needs.</li><li><strong>Try it out for free</strong> and kick-start your Earth Observation application!</li></ul><h3 id="euro-data-cube-in-a-nutshell" tabindex="-1"><strong>Euro Data Cube in a Nutshell</strong> <a class="header-anchor" href="#euro-data-cube-in-a-nutshell" aria-label="Permalink to &quot;**Euro Data Cube in a Nutshell**&quot;">​</a></h3><p>Euro Data Cube combines several services to provide unparalleled EO capabilities:</p><h3 id="data-availability" tabindex="-1"><strong>Data Availability</strong> <a class="header-anchor" href="#data-availability" aria-label="Permalink to &quot;**Data Availability**&quot;">​</a></h3><ul><li><strong>Global archives</strong> of Analysis Ready Data.</li><li><strong>Open satellite missions</strong>: Sentinel, Landsat, MODIS, etc.</li><li><strong>Commercial VHR datasets</strong>: PlanetScope, Pléiades, SPOT, and more.</li><li><strong>Earth System Data Cube</strong> for advanced Earth observation analysis.</li><li><strong>ESA Climate Change Initiative</strong> datasets.</li><li><strong>User-contributed content</strong> to enrich the data ecosystem.</li><li><strong>Data fusion</strong> capabilities to combine multiple datasets.</li></ul><h3 id="access-and-analysis" tabindex="-1"><strong>Access and Analysis</strong> <a class="header-anchor" href="#access-and-analysis" aria-label="Permalink to &quot;**Access and Analysis**&quot;">​</a></h3><ul><li><strong>Sentinel Hub</strong>: A cloud API offering seamless access to key EO datasets, ideal for ad-hoc access, interactive exploration, and integration with third-party applications.</li><li><strong>Batch Processing</strong>: Designed for large-scale analysis and machine learning, perfect for heavy-duty processing tasks.</li><li><strong>xcube</strong>: A customizable tool for analysis and processing based on xarray technology.</li></ul><h3 id="storage-and-distribution" tabindex="-1"><strong>Storage and Distribution</strong> <a class="header-anchor" href="#storage-and-distribution" aria-label="Permalink to &quot;**Storage and Distribution**&quot;">​</a></h3><ul><li><strong>geoDB</strong>: A geospatial database for vector data.</li><li><strong>Sentinel Hub</strong>: For raster data storage and distribution.</li><li><strong>Controlled access</strong>: Manage data distribution to specific users or user groups.</li></ul><h3 id="exploitation" tabindex="-1"><strong>Exploitation</strong> <a class="header-anchor" href="#exploitation" aria-label="Permalink to &quot;**Exploitation**&quot;">​</a></h3><ul><li><strong>EOxHub Workspace</strong>: A managed compute and storage environment to: <ul><li>Run <strong>Jupyter Notebooks</strong>.</li><li>Host <strong>custom applications</strong>.</li></ul></li></ul><h3 id="collaboration" tabindex="-1"><strong>Collaboration</strong> <a class="header-anchor" href="#collaboration" aria-label="Permalink to &quot;**Collaboration**&quot;">​</a></h3><ul><li><strong>Marketplace</strong>: Share data, applications, and algorithms with free or revenue-generating options.</li><li><strong>GitHub repositories</strong>: Contribute and access public code and algorithms.</li></ul><p>Start using Euro Data Cube today to unlock the full potential of Earth Observation!</p><hr><p>TBD</p><ul><li>Configuring Euro Data Cube for Your Workflow</li><li>Connecting to Euro Data Cube: <a href="https://eurodatacube.com" target="_blank" rel="noreferrer">https://eurodatacube.com</a></li><li>Integration Euro Data Cube with EarthCODE Open Science Catalog</li><li>Data Access on Euro Data Cube</li></ul><hr>',22)]))}const b=t(i,[["render",n]]);export{h as __pageData,b as default};
